<pre class="linenums">
function maxWater(arr) {
  var left = 0,
      right = arr.length - 1,
      leftMax = arr[left],
      rightMax = arr[right],
      volume = 0;

  while (left &lt; right) {

    if (leftMax &lt; rightMax) {
      var height = arr[++left];
      leftMax = Math.max(leftMax, height);
      volume += leftMax - height;
    } else {
      var height = arr[--right];
      rightMax = Math.max(rightMax, height);
      volume += rightMax - height;
    }  

  }
  return volume;
}
</pre>

